import chalk from 'chalk';
import { Command } from 'commander';
import open from 'open';

const linkInfoLog = (msg: unknown) => console.log(chalk.yellow(msg));
const linkLogAndOpen = (link: `https://${string}`) => {
	console.log(chalk.green(link));
	open(link);
};

const discordCommand = new Command('discord')
	.description('print discord link')
	.action(() => {
		linkInfoLog('Join the discord to ask questions, report bugs, and look for teammates:');
		linkLogAndOpen('https://discord.gg/SwACArPsMM');
	});

const wikiCommand = new Command('wiki')
	.description('print wiki link')
	.action(() => {
		linkInfoLog('Check out the wiki to learn more about the game and programming your bot:');
		linkLogAndOpen('https://github.com/MechMania-28/Wiki');
	});

const twitchCommand = new Command('twitch').alias('stream')
	.description('print livestream link')
	.action(() => {
		linkInfoLog('Check out the livestream to see your games played!');
		linkLogAndOpen('https://www.twitch.tv/mechmania_tv');
	});

const linkCommands = [discordCommand, wikiCommand, twitchCommand];
export default linkCommands;