import chalk from 'chalk';

export function catastrophicErr(msg: unknown, exitCode = 1){
	const style = chalk.red.bold.underline;
	console.error(style(msg));
	console.error(style('Your command probably failed. Please retry, or contact staff if error persists.'));
	return process.exit(exitCode);
}

export function fatalErr(msg: unknown, exitCode = 1){
	const style = chalk.red;
	console.error(style(msg));
	return process.exit(exitCode);
}

export function unauthedError(err='', exitCode = 1){
	return fatalErr(chalk.red(`Authentication Error: ${err || 'You need to be logged in to run this command!'}`), exitCode);
}

export function forbiddenError(msg: string, exitCode = 1){
	return fatalErr(`Command failed: ${msg}`, exitCode);
}

export function notLoggedInErr(){
	return fatalErr('You must be logged in to use this command!');
}